    public int findLength(int[] a, int[] b) {

        int max = 0;

        Map<String, Boolean> map = new HashMap<>();

        for (int i = 0; i <= a.length; i++) {

            for (int j = 0; j < i; j++) {
                if(!map.containsKey(j + ":" + i)){
                    boolean contains = containsSubArray(b, getSubarray(a, j, i));
                    map.put(j + ":" + i, contains);
                    if (contains) {
//                    println(Arrays.toString(getSubarray(a, j, i)));
                        max = Math.max(max, i - j);
                    }
                }

            }
        }

        return max;
    }

    private boolean containsSubArray(int[] array, int[] subarray) {

        for (int i = 0; i <= array.length - subarray.length; i++) {
            if (array[i] == subarray[0]) {
                if (sequenceMatches(array, subarray, i)) {
//                    println("sequenceMatches for " + i);
                    return true;
                }
            }
        }

        return false;
    }

    private boolean sequenceMatches(int[] array, int[] subarray, int i) {
        boolean candidate = true;
        for (int j = i; j < subarray.length + i; j++) {
            if (array[j] != subarray[j - i]) {
                candidate = false;
                break;
            }
        }
        return candidate;
    }

    public int[] getSubarray(int[] a, int i, int j) {
        int[] res = new int[j - i];

        for (int k = i; k < j; k++) {
            res[k - i] = a[k];
        }
        return res;
    }


